mapper.select:
SELECT t1.*, t2.create_date AS report_time FROM collect_flow_log t1 LEFT JOIN collect_flow t2 ON t1.area_id = t2.area_id AND t1.year = t2.year
and t1.year=#{year}
and t1.area_id in
#{areaId}
ORDER BY year,create_time desc

SELECT
  t1.*,
  t2.create_date AS report_date
FROM
  collect_flow_log t1
LEFT JOIN collect_flow t2 ON t1.area_id = t2.area_id AND t1.year = t2.year
where t1.area_id = #{areaId}

  and t1.year=#{year}


  and t1.operation=#{status}

order by year,create_time desc limit 1 OFFSET 0
